{#<a class=\"btn btn-info\" style=\"margin:10px;\" href=\"{{ path(\"app_logout\") }}\">Cerrar sesión</a>#}

{# Recorrido de los sistemas del usuario, recibido por parametro #}

    <div>
        <h3 style=\"text-align:center;\">Sistemas >> Editar sistema</h3>
    </div>
    <div style=\"background-color:white\" class=\"container formularioSistema\">
        <div class=\"row\">
        <div class=\"col-xs-4 col-md-4\">
            <img class=\"card-img-top rounded imagenSistema\" id=\"imagen\" src=\"{{asset(('uploads/'~ imagen))}}\" alt=\"Imagen del sistema\">
        </div>
        <div class=\"col-xs-8 col-md-8\">
    {{ form_start(formulario, {'enctype':'multipart/form-data'})}}
        <div style=\"margin-top: 30px;\" class=\"form-group row\">
            <div class=\"col-sm-2 col-form-label\">
                <h5>Nombre:</h5>
            </div>
            <div class=\"col-sm-10 espaciado\">
                {{ form_widget(formulario.nombre, {attr: {class: 'form-control campoForm'} })}}
            </div>
        </div>
        <div class=\"form-group row\">
            <div class=\"col-sm-2 col-form-label\">
                <h5>Imágen del sistema:</h5>
            </div>
            <div class=\"col-sm-10 espaciado \">
                {{ form_widget(formulario.logo)}}
            </div>
        </div>
        <div class=\"form-group row\">
            <div class=\"col-sm-2 col-form-label\">
                <h5>URL:</h5>
            </div>
            <div class=\"col-sm-10 espaciado \">
                {{ form_widget(formulario.url, {attr: {class: 'form-control campoForm'} })}}
            </div>
        </div>
        <div class=\"form-group row\">
            <div class=\"col-sm-2 col-form-label\">
                <h5>Descripción:</h5>
            </div>
            <div class=\"col-sm-10 espaciado \">
                {{ form_widget(formulario.descripcion, {attr: {class: 'form-control campoForm'} })}}
            </div>
        </div>
    </div>
            {{ form_widget(formulario.Crear, {attr: {class: 'container-fluid btn btn-primary colorBoton'} })}}
    {{ form_end(formulario) }}  
    </div>
    
    </div>
    
    {% block notificaciones %}
        {{ parent() }}
    {% endblock notificaciones %}

{% endblock %}
